(110, 1, '2018-12-28 23:14:02', '2018-12-28 23:14:02', '{\"id\":\"110\",\"field_id\":5,\"fields\":[{\"id\":\"0\",\"type\":\"name\",\"label\":\"Name\",\"format\":\"first-last\",\"description\":\"\",\"required\":\"1\",\"size\":\"medium\",\"simple_placeholder\":\"\",\"simple_default\":\"\",\"first_placeholder\":\"e.g. Gurinder\",\"first_default\":\"\",\"middle_placeholder\":\"\",\"middle_default\":\"\",\"last_placeholder\":\"e.g. Singh\",\"last_default\":\"\",\"css\":\"\"},{\"id\":\"1\",\"type\":\"email\",\"label\":\"Email\",\"description\":\"\",\"required\":\"1\",\"size\":\"medium\",\"placeholder\":\"e.g. singh.gurinder@student.howest.be\",\"confirmation_placeholder\":\"\",\"default_value\":\"\",\"css\":\"\"},{\"id\":\"2\",\"type\":\"textarea\",\"label\":\"Your message\",\"description\":\"\",\"required\":\"1\",\"size\":\"medium\",\"placeholder\":\"e.g. Hey I\'d like to discuss about something with you....\",\"css\":\"\"}],\"settings\":{\"form_title\":\"Contact-Forum\",\"form_desc\":\"\",\"form_class\":\"\",\"submit_text\":\"Submit\",\"submit_text_processing\":\"Sending...\",\"submit_class\":\"\",\"honeypot\":\"1\",\"notification_enable\":\"1\",\"notifications\":{\"1\":{\"email\":\"{admin_email}\",\"subject\":\"WP portfolio contact forum\",\"sender_name\":\"{field_id=\\\"0\\\"}\",\"sender_address\":\"{admin_email}\",\"replyto\":\"{field_id=\\\"1\\\"}\",\"message\":\"{all_fields}\"}},\"confirmations\":{\"1\":{\"type\":\"message\",\"message\":\"<p>Thanks for contacting us! I will be in touch with you as soon as possible.<\\/p>\",\"message_scroll\":\"1\",\"page\":\"59\",\"redirect\":\"\"}}},\"meta\":{\"template\":\"contact\"}}', 'Contact-Forum', '', 'publish', 'closed', 'closed', '', 'contact-forum', '', '', '2018-12-29 12:27:56', '2018-12-29 12:27:56', '', 0, 'http://localhost/wordpress/?post_type=wpforms&#038;p=110', 0, 'wpforms', '', 0),
